// Local wrapper for pdf.js worker. Falls back to CDN if bundled worker code is unavailable.
try {
  importScripts('https://cdnjs.cloudflare.com/ajax/libs/pdf.js/4.4.168/pdf.worker.min.js');
} catch (err) {
  console.error('Failed to load pdf.worker.min.js from cdnjs:', err);
  try {
    importScripts('https://unpkg.com/pdfjs-dist@4.4.168/build/pdf.worker.min.js');
  } catch (fallbackError) {
    console.error('Failed to load pdf.worker.min.js from unpkg:', fallbackError);
    throw fallbackError;
  }
}
